import React, { useState } from 'react';
import { Container } from 'react-bootstrap';
import ImgFullScreen from '../imgFullScreen/imgFullScreen';
import { ReactComponent as Arrows } from '../../assets/arrows-up-down-left-right.svg'

export default function ImgContainer({ usedIds, getSrc, getPhotographer }) {
    const [show, setShow] = useState(false);

    return (
        <Container fluid>
            <div className='d-flex flex-wrap justify-content-center box-container'>
                {usedIds.map(index => {
                    return (
                        <div className='box' key={index}>
                            <img className='img-fluid' alt='random image' src={getSrc(index, 'medium')} />
                            <div>
                                <p>{getPhotographer(index)}</p>
                                <ImgFullScreen
                                    show={show}
                                    setShow={setShow}
                                    title={getPhotographer(index)}
                                    src={getSrc(index, 'large2x')}
                                    hrefDownload={getSrc(index, 'original')}
                                ></ImgFullScreen>
                                <Arrows />
                            </div>
                            <button type="button" onClick={() => setShow(true)}>BOTON</button>
                        </div>
                    )
                })}
            </div>
        </Container>
    );
};

