class NavHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = { borderNavbar: false, formCollapse: false, mainToggle: false, state: '' };
    }
    // this.state.state : '' | 'contact' | 'active'

    borderNavbar = () => {
        if (window.scrollY > 0) {
            this.setState({ borderNavbar: true });
        } else {
            this.setState({ borderNavbar: false });
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.borderNavbar, true);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.borderNavbar);
    }

    mainToggle = () => {
        if (this.state.state === '') {
            this.setState({ state: 'active' });
        } else {
            this.setState({ state: '' });
        }
    }

    formCollapse = () => {
        if (this.state.state === 'contact') {
            this.setState({ state: '' });
        } else {
            this.setState({ state: 'contact' });
        }
    }

    render() {
        let estado = this.state.state;
        let formCollapse = false;
        let navEstado = '';
        if (estado === 'contact') {
            navEstado = 'fullHeight';
            formCollapse = true;
        }

        return (
            <nav className={`sticky-top blur border-bottom-transition flex-column ${this.state.borderNavbar ? 'border-bottom-active' : ''} ${navEstado}`}>
                <Container fluid>
                    <div className='flexDirection justify-content-between align-content-center'>
                        <div className='d-flex justify-content-between align-content-center'>
                            <a className="navbar-brand text-secondary text-hover-color logo" href="#">R-IMAGE</a>
                            <button type="button" className='nav-link active text-center text-hover-color navButton d-lg-none' onClick={this.mainToggle} >
                                {estado === '' ? <Bars className='navIcon' /> : <Xmark className='navIcon' />}
                            </button>
                        </div>
                        <NavbarCategorias activeCategory={this.props.activeCategory} categoriesList={this.props.categoriesList} categoriesCallback={this.props.categoriesCallback} className={(estado === '') ? 'navCollapse' : 'navShow'} />
                        <Contact active={formCollapse} callback={this.formCollapse} className={(estado === '') ? 'navCollapse' : 'navShow'} />
                    </div>
                    <ContactForm display={formCollapse} />
                </Container>
            </nav>
        );
    }
}


export default NavHeader;
