import React from 'react';
import './NavHeader.css';
import { Navbar } from 'react-bootstrap';

class NavHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = { borderNavbar: false };
    }

    borderNavbar = () => {
        if (window.scrollY > 0) {
            this.setState({ borderNavbar: true });
        } else {
            this.setState({ borderNavbar: false });
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.borderNavbar, true);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.borderNavbar);
    }

    render() {
        return (
            <Navbar collapseOnSelect expand='lg' sticky='top' className={`blur border-bottom-transition ${this.state.borderNavbar ? 'border-bottom-active' : ''}`}>
                <div className='container-fluid'>
                    <a className="navbar-brand text-secondary text-hover-color" href="#"><h1>R-IMAGE</h1></a>

                    <Navbar.Toggle aria-controls='responsive-navbar-nav' />

                    <Navbar.Collapse className='justify-content-end'>
                        <h4 className='text-center text-secondary d-lg-none'>CATEGORIES</h4>
                        {this.props.children}
                    </Navbar.Collapse>

                    <Navbar.Collapse className='justify-content-end'>
                        <a className="nav-link active text-secondary text-center text-hover-color" aria-current="page" href="./pages/contacto.html"><h4>CONTACT</h4></a>
                    </Navbar.Collapse>
                </div>
            </Navbar>
        );
    }
}

export default NavHeader;
