import React, { useState } from 'react';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import { ReactComponent as DownloadIcon } from '../../assets/circle-down.svg'

export default function Download(props) {
    const [link, setlink] = useState(false);

    const renderTooltip = (props) => (
        <Tooltip id="button-tooltip" {...props}>
            Download
        </Tooltip>
    );

const onButtonClick = () => {
    fetch(props.href).then(response => {
        response.blob().then(blob => {
            let url = window.URL.createObjectURL(blob);
            let a = document.createElement('a');
            a.href = url;
            a.download = props.title;
            a.click();
        });
    });
}

    const simulateClick = (e) => {
        e.click();
        setlink(false);
    }

    return (
        <>
            <OverlayTrigger
                placement="right"
                delay={{ show: 250, hide: 400 }}
                overlay={renderTooltip}
            >
                {
                    (link === false) ?
                        <button type="button" className='customButton' onClick={onButtonClick}><DownloadIcon /></button> :
                        link
                }
            </OverlayTrigger>
        </>
    );
}
