import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';

export default function ImgFullScreen(props) {
    const [show, setShow] = useState(false);

    return (
        <>
            <button type="button" class="" onClick={() => setShow(true)}>{props.children}</button>
            <Modal show={show} fullscreen={true} onHide={() => setShow(false)} className='ImgFullScreen'>
                <Modal.Body closeButton variant="white"><img className='img-fluid' alt='random image' src={props.src} ></img></Modal.Body>
                <Modal.Header closeButton>
                </Modal.Header>
                <Modal.Footer><Modal.Title>{props.title}</Modal.Title></Modal.Footer>
            </Modal>
        </>
    );
}
