import React from 'react';
import './App.css';
import pexelsAuth from './components/secrets/secrets';
import NavHeader from './components/NavHeader/NavHeader';
import NavbarCategorias from './components/NavbarCategorias/NavbarCategorias';
import ImgContainer from './components/ImgContainer/ImgContainer';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { categoriesList: ['landscape', 'city', 'sky', 'nature', 'forest', 'sea', 'beach', 'mountain'], pexelsPerPage: 50, defaultNumImg: 4, activeCategory: 'landscape', categories: {}, usedIds: [], photos: {} };
    this.state.categoriesList.forEach(category => this.state.categories[category] = { page: 0, next_page: '', availableIds: [] });
  }

  isIndUsedIds(id) {
    return this.state.usedIds.includes(id);
  }

  isInCategories(id) {
    return Object.values(this.state.categories).some((category) => (category.availableIds.includes(id)));
  }

  push(category, json) {
    this.state.categories[category].next_page = json.next_page;
    this.state.categories[category].page = json.page;
    json.photos.forEach(photo => {
      if (this.isIndUsedIds(photo.id) === false && this.isInCategories(photo.id) === false) {
        this.state.categories[category].availableIds.push(photo.id);
        this.state.photos[photo.id] = photo;
      }
    })
  }

  getRandom(category, cantidad) {
    const disponibles = () => this.state.categories[category].availableIds.length;
    let random = [];
    let ciclos = cantidad < disponibles() ? cantidad : disponibles();
    while (random.length < ciclos) {
      let index = Math.floor(Math.random() * (disponibles() - 1));
      if (!random.includes(this.state.categories[category].availableIds[index])) {
        let id = this.state.categories[category].availableIds.splice(index, 1).pop();
        random.push(id);
        this.state.usedIds.push(id);
      }
      this.setState({ usedIds: this.state.usedIds });
    }
    this.state.usedIds.push()
    return random;
  }

  getSrc = (id, type) => {
    return this.state.photos[id].src[type];
  }

  getNextPage(category) {
    return this.state.categories[category].next_page;
  }

  getJson = async (category, url) => {
    try {
      url = (url == '') ? `https://api.pexels.com/v1/search?query=${category}&per_page=${this.state.pexelsPerPage}` : url;
      let res = await fetch(url, { headers: { Authorization: pexelsAuth() } });
      let json = await res.json();

      if (!res.ok) throw { status: res.status, statusText: res.statusText };

      return json;

    } catch (err) {
      let message = err.statusText || "Ocurrió un error";
      console.log(`Error ${err.status}: ${message}`);
      return {};
    }
  }

  getPhotos = async (category, cantidad) => {
    if (this.state.categories[category].availableIds.length < cantidad) await this.push(category, await this.getJson(category, this.getNextPage(category)));
    return this.getRandom(category, cantidad);
  }

  addToImgContainer = async (category, num_img) => {
    this.setState({ activeCategory: category });
    await this.getPhotos(category, num_img);
    //this.forceUpdate()
  }

  infiniteScroll = () => {
    if (window.scrollY + window.innerHeight >= document.documentElement.scrollHeight) this.addToImgContainer(this.state.activeCategory, this.state.defaultNumImg);
  }

  componentDidMount() {
    this.addToImgContainer(this.state.activeCategory, this.state.defaultNumImg);

    window.onscroll = () => {
      this.infiniteScroll();
    }
  }

  render() {
    //console.log(this.state);
    //console.log(this.state.usedIds);
    return (
      <div>
        <NavHeader>
          <NavbarCategorias activeCategory={this.state.activeCategory} categoriesList={this.state.categoriesList} categoriesCallback={this.addToImgContainer} />
        </NavHeader>
        <ImgContainer usedIds={this.state.usedIds} getSrc={this.getSrc} />
      </div>
    );
  }

}

export default App;
