import React from 'react';
import './NavHeader.css';
import { Navbar, Container } from 'react-bootstrap';
import Contact from '../Contact/Contact';

class NavHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = { borderNavbar: false, extra: '' };
        const MyContext = React.createContext(this.state.extra);
    }

    borderNavbar = () => {
        if (window.scrollY > 0) {
            this.setState({ borderNavbar: true });
        } else {
            this.setState({ borderNavbar: false });
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.borderNavbar, true);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.borderNavbar);
    }

    extra = (content) => {
        this.setState({ extra: content });
    }

    render() {
        return (
            <Navbar collapseOnSelect expand='lg' sticky='top' className={`blur border-bottom-transition flex-column ${this.state.borderNavbar ? 'border-bottom-active' : ''}`}>
                <Container fluid>
                    <a className="navbar-brand text-secondary text-hover-color" href="#"><h1>R-IMAGE</h1></a>
                    <Navbar.Toggle aria-controls='responsive-navbar-nav' />
                    {this.props.children}
                    <Contact extra={this.extra} />
                    {this.state.extra}
                    <div className={`formContainer contactFormTransition ${this.state.collapse ? 'contactFormActive' : ''}`}>
                <form action="https://formspree.io/f/moqbjpjj" method="post" className='formRegister'>
                    <input type="text" placeholder="Nombre" name="name" id="name" required class="input" />
                    <input type="text" placeholder="Apellido" name="lastname" id="lastname" class="input" />
                    <input type="email" name="email" id="email" placeholder="Email" required class="input" />
                    <textarea name="mensaje" id="mensaje" cols="20" rows="5" minlength="2" maxlength="255"
                        placeholder="Mensaje" required class="input textarea"></textarea>
                    <input type="submit" value="Enviar" class="submit" />
                </form>
            </div>
                </Container>

            </Navbar>
        );
    }
}
/*
                    <Container fluid className='d-flex justify-content-around'>
                        {this.state.extra}
                    </Container>
*/
export default NavHeader;
