import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";
import { ReactComponent as MagnifyingGlass } from './magnifying-glass.svg'

export default function SearchBar({ activeCategory}) {
    const { register, formState: { errors }, watch, handleSubmit, reset } = useForm("");
    const maxLength = 80;

    const onSubmit = (params) => {
        reset();
    }

    watch((data) => console.log(data));

    return (
        <form onSubmit={handleSubmit(onSubmit)} className='search-bar-form'>
            <input type="text" placeholder={activeCategory} maxLength={maxLength} id="searchField" {...register('searchField', { required: true, maxLength: maxLength })} />
            <label>
                <input type="submit" className='d-none' />
                <MagnifyingGlass/>
            </label>
        </form>
    );
};
