import React from 'react';

export default function NavbarCategorias({activeCategory, categoriesList, categoriesCallback, stateActive, className, ulClassName}) {
    return (
        <div className={`justify-content-end flexDirection ${className}`}>
            <button type='button' className='nav-link active text-center text-hover-color customButton text-secondary d-lg-none' onClick={stateActive}>CATEGORIES</button>
        <ul className={`navbarNav  ${ulClassName}`}>
            {categoriesList.map((category, index) => {
                let buttonClassName = `btn-nav bg-transparent text-hover-color ${(activeCategory == category) ? 'button-active' : 'text-secondary'}`
                return (
                    <li className='text-center' key={index}>
                        <button type='button' className={buttonClassName} onClick={() => categoriesCallback(category, 6)} id={category}>
                            {`${category.charAt(0).toUpperCase() + category.slice(1)}`}
                        </button>
                    </li>
                );
            })}
        </ul>
        </div>
    );
}
