import './App.css';
import NavHeader from './NavHeader';
import ImgContainer from './ImgContainer';

let categories_list = ['landscape', 'city', 'sky', 'nature', 'forest', 'sea','beach','mountain'];
let active_category = 'landscape';
let default_num_img = 12;
let pexels_per_page = 50;

class dataClass {
  constructor(categories_list) {
      this.categories = {}
      categories_list.forEach( category => this.categories[category] = {page: 0, next_page:'', availableIds:[]} );
      this.usedIds = [];
      this.photos = {};
  }
  isIndUsedIds(id){
      return this.usedIds.includes(id);
  }
  isInCategories(id){
      return Object.values(this.categories).some( (category)=>( category.availableIds.includes(id) ) );
  }
  push(category,json) {
      this.categories[category].next_page = json.next_page;
      this.categories[category].page = json.page;
      json.photos.forEach( photo => {
          if(this.isIndUsedIds(photo.id) === false && this.isInCategories(photo.id) === false) {
              this.categories[category].availableIds.push(photo.id);
              this.photos[photo.id] = photo;
          }
      })
  }
  getRandom(category, cantidad) {
      const disponibles = () => this.categories[category].availableIds.length;
      let random = [];
      let ciclos = cantidad < disponibles() ? cantidad : disponibles();
      while (random.length < ciclos) {
          let index = Math.floor(Math.random() * ( disponibles() - 1 ) );
          if (!random.includes(this.categories[category].availableIds[index])){
              let id = this.categories[category].availableIds.splice(index,1).pop();
              random.push(id);
              this.usedIds.push(id);
          }
      }
      this.usedIds.push()
      return random;
  }
  getSrc(id,type){
      return this.photos[id].src[type];
  }
  getNextPage(category){
      return this.categories[category].next_page;
  }
  remove(ids){
      ids.forEach(id => delete this.photos[id]);
  }
}


const getJson = async (category, url) => {
  try {
      url = (url == '') ? `https://api.pexels.com/v1/search?query=${category}&per_page=${pexels_per_page}` : url;
      let res = await fetch(url, { headers: { Authorization: '563492ad6f91700001000001c6df12244f84428fa8467b09ec5d6ff9' } });
      let json = await res.json();
      //console.log(res, json);

      if (!res.ok) throw { status: res.status, statusText: res.statusText };

      return json;

  } catch (err) {
      let message = err.statusText || "Ocurrió un error";
      console.log(`Error ${err.status}: ${message}`);
      return {};
  }
}

const getPhotos = async (category, cantidad) => {
  if (data.categories[category].availableIds.length < cantidad) await data.push(category, await getJson(category, data.getNextPage(category)));
  return data.getRandom(category, cantidad);
}



