import React from 'react';
import './NavbarCategorias.css'
import { Navbar } from 'react-bootstrap';

function NavbarCategorias({ activeCategory, categoriesList, categoriesCallback }) {
    return (
        <Navbar.Collapse className='justify-content-end'>
<Navbar collapseOnSelect id='navbarCategorias'>

<button class="navbar-toggler text-secondary " type="button" data-bs-toggle="collapse"
                            data-bs-target="#navbarCategorias" aria-controls="navbarCategorias"
                            aria-expanded="false" aria-label="Toggle navigation">
                            <h4 class="">CATEGORIES</h4> 
                        </button>
<Navbar.Collapse>
            <ul className='navbar-nav'>
                {categoriesList.map((category, index) => {
                    let buttonClassName = `btn bg-transparent text-hover-color ${(activeCategory == category) ? 'button-active' : 'text-secondary'}`
                    return (
                        <li className='nav-item text-center' key={index}>
                            <button className={buttonClassName} type='button' onClick={() => categoriesCallback(category, 6)} id={category}>
                                {`${category.charAt(0).toUpperCase() + category.slice(1)}`}
                            </button>
                        </li>
                    );
                })}
            </ul>
            </Navbar.Collapse>
            </Navbar>
        </Navbar.Collapse>
    );
}

export default NavbarCategorias;

