import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";
import { ReactComponent as MagnifyingGlass } from './magnifying-glass.svg'

export default function SearchBar({activeCategory, categoriesList, setSearchField, categoriesCallback, className}) {
    const { register, formState: { errors }, watch, handleSubmit, reset } = useForm("");
    const maxLength = 80;

    const filter = (searchField) => {
        const startsWith = [];
        const includes = [];
        const others = [];

        categoriesList.forEach(category => {
            if(category.startsWith(searchField)){
                startsWith.push(category);
            }else if(category.includes(searchField)){
                includes.push(category);
            }else{
                others.push(category);
            }

        });

        return {startsWith:startsWith, includes:includes, others:others}
        /*
        let li = categoriesList.filter(category => category.startsWith(searchField)); 
        if (li.length === 0) {
            li = categoriesList.filter(category => category.includes(searchField));
        }
        return li;*/
    }

    const onSubmit = ({searchField}) => {
        const {startsWith, includes} = filter(searchField);
        const li = startsWith.concat(includes);
        console.log(searchField+': ');
        if( li.length != 0 ){
            console.log(li);
            categoriesCallback(li.shift(), 6);
        }
        reset();
    }

    watch(({searchField}) => {
        setSearchField(searchField)
    });

    return (
        <form onSubmit={handleSubmit(onSubmit)} className={`search-bar-form ${className}`}>
            <input type="text" placeholder={activeCategory} maxLength={maxLength} id="searchField" {...register('searchField', { required: true, maxLength: maxLength })} />
            <label>
                <input type="submit" className='d-none' />
                <MagnifyingGlass/>
            </label>
        </form>
    );
};
