/* VARIABLES GLOBALES */

let categories_list = ['landscape', 'city', 'sky', 'nature', 'forest', 'sea','beach','mountain'];
let active_category = 'landscape';
let default_num_img = 12;
let pexels_per_page = 50;

/* FUNCIONES */

function getCategoriesList(){
    return categories_list;
}


const border_bottom_nav = () => {
    if ( !document.getElementById("headerNav") ) return;
    if (window.scrollY > 0){
        document.getElementById("headerNav").classList.add('border-bottom-active');
    }else{
        document.getElementById("headerNav").classList.remove('border-bottom-active');
    }
} 

const infiniteScroll = () => {
    if (window.scrollY + window.innerHeight >= document.documentElement.scrollHeight) add_to_img_container(active_category, default_num_img, false);
}

const fill_navbarCategorias = () => {
    const $navbarCategorias = document.getElementById("navbarCategorias");
    if (!$navbarCategorias) return;
    let ul = document.createElement("ul");
    ul.className = 'navbar-nav';

    categories_list.forEach( category => {
        let button = document.createElement("button");
        button.id = category;
        button.className = 'btn text-secondary bg-transparent text-hover-color';
        button.type = 'button';
        button.onclick = () => add_to_img_container(category,6);
        button.innerHTML = `${category.charAt(0).toUpperCase() + category.slice(1)}`;

        let li = document.createElement("li");
        li.className = 'nav-item text-center';
        li.appendChild(button);
        ul.appendChild(li);
    });

    $navbarCategorias.appendChild(ul);
}

const getJson = async (category, url) => {
    try {
        url = (url == '') ? `https://api.pexels.com/v1/search?query=${category}&per_page=${pexels_per_page}` : url;
        let res = await fetch(url, { headers: { Authorization: '563492ad6f91700001000001c6df12244f84428fa8467b09ec5d6ff9' } });
        let json = await res.json();
        //console.log(res, json);

        if (!res.ok) throw { status: res.status, statusText: res.statusText };

        return json;

    } catch (err) {
        let message = err.statusText || "Ocurrió un error";
        console.log(`Error ${err.status}: ${message}`);
        return {};
    }
}

const getPhotos = async (category, cantidad) => {
    if (data.categories[category].availableIds.length < cantidad) await data.push(category, await getJson(category, data.getNextPage(category)));
    return data.getRandom(category, cantidad);
}

