        this.categories = {}
        categories_list.forEach( category => this.categories[category] = {page: 0, next_page:'', availableIds:[]} );
        this.usedIds = [];
        this.photos = {};
    }
    isIndUsedIds(id){
        return this.usedIds.includes(id);
    }
    isInCategories(id){
        return Object.values(this.categories).some( (category)=>( category.availableIds.includes(id) ) );
    }
    push(category,json) {
        this.categories[category].next_page = json.next_page;
        this.categories[category].page = json.page;
