import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";
import { ReactComponent as MagnifyingGlass } from './magnifying-glass.svg'

export default function SearchBar({ activeCategory, categoriesList, setFilteredCategories, categoriesCallback, className }) {
    const { register, formState: { errors }, watch, handleSubmit, reset } = useForm("");
    const maxLength = 80;

    const filter = (searchField) => {
        if (!(searchField)) return [];

        const startsWith = [];
        const includes = [];

        categoriesList.forEach(category => {
            if (category.startsWith(searchField)) {
                startsWith.push(category);
            } else if (category.includes(searchField)) {
                includes.push(category);
            }
        });

        return startsWith.concat(includes);
    }

    const onSubmit = ({ searchField }) => {
        const li = filter(searchField);

        if (li.length != 0) {
            console.log(li);
            categoriesCallback(li.shift(), 6);
            setFilteredCategories([]);
        }

        reset();
    }

    watch(({ searchField }) => {
        const li = filter(searchField);
        console.log('watch: ', li);
        setFilteredCategories(li);
    });

    return (
        <form onSubmit={handleSubmit(onSubmit)} className={`search-bar-form ${className}`}>
            <input type="text" placeholder={activeCategory} maxLength={maxLength} autoComplete="off" id="searchField" {...register('searchField', { required: true, maxLength: maxLength })} />
            <label>
                <input type="submit" className='d-none' />
                <MagnifyingGlass />
            </label>
        </form>
    );
};
