import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import { ReactComponent as Download } from '../../assets/circle-down.svg'

export default function ImgFullScreen(props) {
    const [show, setShow] = useState(false);
    const [link, setlink] = useState(false);

    const renderTooltip = (props) => (
        <Tooltip id="button-tooltip" {...props}>
            Download
        </Tooltip>
    );

    const onButtonClick = () => {
        // using Java Script method to get PDF file
        fetch(props.hrefDownload).then(response => {
            response.blob().then(blob => {
                // Creating new object of PDF file
                const fileURL = window.URL.createObjectURL(blob);
                // Setting various property values
                setlink(<a ref={simulateClick} href={fileURL} download={props.title} target="_blank">Descargar</a>)
                //alink.click();
            })
        })
    }

    const simulateClick = (e) => {
        e.click();
    }

    return (
        <>
            <button type="button" onClick={() => setShow(true)}>{props.children}</button>
            <Modal show={show} fullscreen={true} onHide={() => setShow(false)} className='ImgFullScreen'>
                <Modal.Body closeButton variant="white"><img className='img-fluid' alt='random image' src={props.src} ></img></Modal.Body>
                <Modal.Header closeButton>
                </Modal.Header>
                <Modal.Footer>
                    <Modal.Title>{props.title}</Modal.Title>
                    <OverlayTrigger
                        placement="right"
                        delay={{ show: 250, hide: 400 }}
                        overlay={renderTooltip}
                    >
                        {
                            (link === false) ?
                                <button type="button" className='customButton' onClick={onButtonClick}><Download /></button> :
                                link
                        }
                    </OverlayTrigger>
                </Modal.Footer>
            </Modal>
        </>
    );
}

