import React, { useState } from 'react';
import ImgFullScreen from '../imgFullScreen/imgFullScreen';
import { ReactComponent as Arrows } from '../../assets/arrows-up-down-left-right.svg'

export default function ImgBox({title, src, hrefDownload}) {
    const [show, setShow] = useState(false);

    return (
        <>
            <img className='img-fluid' alt='random image' src={getSrc(index, 'medium')} />
            <div>
                <p>{title}</p>
                <ImgFullScreen
                    show={show}
                    setShow={setShow}
                    title={title}
                    src={src}
                    hrefDownload={hrefDownload}
                ></ImgFullScreen>
                <Arrows />
            </div>
            <button type="button" onClick={() => setShow(true)}>{title}</button>
        </>
    )
}
