import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";

const ContactForm = ({ display = true }) => {
    const [sended, setSended] = useState(2);
    const { register, formState: { errors }, handleSubmit } = useForm("");
    //const onSubmit = data => console.log(data);

    const onSubmit = async (data) => {
        try {
            const response = await fetch('https://formspree.io/f/moqbjpjj', {
                method: 'post',
                body: JSON.stringify(data),
                headers: {
                    'Accept': 'application/json'
                }
            });
            const jsonData = await response.json();
            setSended( jsonData.ok == true ? true : false );
        } catch {
            setSended(false);
        }
    }

    let showStatus = false;
    const showError = () => {
        if (showStatus) {
            return false;
        } else {
            showStatus = true;
            return true;
        }
    }

    useEffect(() => {
        showStatus = false;
    });

    const errorMessage = (error, type, message) => error?.type === type && showError() && <p className="errorText">{message}</p>;

    return (
        <div className={`text-center formContainer  ${display ? 'contactFormActive' : 'contactFormTransition'}`}>
            <h3 className='text-secondary formTitle' >Contact Us</h3>
            <form onSubmit={handleSubmit(onSubmit)} method="post" className='formRegister'>
                <input type="text" placeholder="Nombre" maxLength="50" id="name" {...register('name', { required: true, maxLength: 50 })} className="input" />
                {errorMessage(errors.name, 'required', 'El campo Nombre es obligatorio')}
                <input type="text" placeholder="Apellido" maxLength="50" id="lastname" {...register('lastname', { required: true, maxLength: 50 })} className="input" />
                {errorMessage(errors.lastname, 'required', 'El campo Apellido es obligatorio')}
                <input type="text" placeholder="Email" maxLength="50" id="email" {...register('email', { required: true, pattern: /^\w+([.-_+]?\w+)*@\w+([.-]?\w+)*(\.\w{2,10})+$/, maxLength: 50 })} className="input" />
                {errorMessage(errors.email, 'required', 'El campo Email es obligatorio')}
                {errorMessage(errors.email, 'pattern', 'El formato es incorrecto')}
                <textarea placeholder="Mensaje" maxLength="300" minLength="10" cols="20" rows="5" id="message" {...register('message', { required: true, minLength: 2, minLength: 10, maxLength: 300 })} className="input textarea"></textarea>
                {errorMessage(errors.message, 'required', 'El campo Mensaje es obligatorio')}
                <input type="submit" value="Enviar" className="submit" />
                {(sended == true) ? <p className='sendedOk'>Mensaje enviado con Exito</p> : (sended == false) ? <p className='sendedFail'>Hubo un problema</p> : ''}
            </form>
        </div>
    );
};

