import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import { ReactComponent as Download } from '../../assets/circle-down.svg'

export default function ImgFullScreen(props) {
    const [show, setShow] = useState(false);
    const [link, setlink] = useState(false);

    const renderTooltip = (props) => (
        <Tooltip id="button-tooltip" {...props}>
            Full Size Image
        </Tooltip>
    );

    return (
        <>
            <button type="button" onClick={() => setShow(true)}>{props.children}</button>
            <Modal show={show} fullscreen={true} onHide={() => setShow(false)} className='ImgFullScreen'>
                <Modal.Body closeButton variant="white"><img className='img-fluid' alt='random image' src={props.src} ></img></Modal.Body>
                <Modal.Header closeButton>
                </Modal.Header>
                <Modal.Footer>
                    <Modal.Title>{props.title}</Modal.Title>
                    <OverlayTrigger
                        placement="right"
                        delay={{ show: 250, hide: 400 }}
                        overlay={renderTooltip}
                    >
                        <a href={props.hrefDownload} target="_blank"><Download /></a>
                    </OverlayTrigger>
                </Modal.Footer>
            </Modal>
        </>
    );
}
