import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Download from '../Download/Download';
import ImgFullScreen from '../ImgFullScreen/ImgFullScreen';
import { ReactComponent as Arrows } from '../../assets/arrows-up-down-left-right.svg'

export default function ImgBox({ index, title, src, srcFullScreen, hrefDownload }) {
    const [show, setShow] = useState(false);
    //console.log('show: ',show)

    return (
        <div className='box' key={index}>
            <img className='img-fluid' alt='random image' src={src} />
            <button type="button" onClick={() => setShow(!show)}>
                <p>{title}</p>
                <Modal show={show} fullscreen={true} onHide={() => setShow(false)} className='ImgFullScreen'>
                    <Modal.Body>
                        <img className='img-fluid' alt='random image' src={src} />
                        <div></div>
                    </Modal.Body>
                    <Modal.Header closeButton></Modal.Header>
                </Modal>
                <Arrows />
            </button>
            {show ?
                <Modal.Footer>
                    <Modal.Title>{title}</Modal.Title>
                    <Download title={title} href={hrefDownload} />
                </Modal.Footer>
                : <></>}
        </div>
    )
}

