import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Download from '../Download/Download';

export default function ImgFullScreen({show, setShow, title, src, hrefDownload}) {

    return (
        <>
            <Modal show={show} fullscreen={true} onHide={() => setShow(false) && console.log('show: ',show)} className='ImgFullScreen'>
                <Modal.Body closeButton variant="white">
                    <img className='img-fluid' alt='random image' src={src} />
                    <div></div>
                </Modal.Body>
                <Modal.Header closeButton></Modal.Header>
                <Modal.Footer>
                    <Modal.Title>{title}</Modal.Title>
                    <Download title={title} href={hrefDownload}/>
                </Modal.Footer>
            </Modal>
        </>
    );
}

