import React from 'react';
import { Container } from 'react-bootstrap';
import { ReactComponent as CircleChevron } from './circle-chevron-right.svg'

export default function ImgContainer({ usedIds, getSrc, getPhotographer }) {
    return (
        <Container fluid>
            <div className='d-flex flex-wrap justify-content-center box-container'>
                {usedIds.map(index => {
                    return (
                        <div className='box' key={index}>
                            <img className='img-fluid' alt='random image' src={getSrc(index, 'medium')} ></img>
                            <div>
                                <p>{getPhotographer(index)}</p>
                                <a href={getSrc(index, 'large2x')} target='_blank'> <CircleChevron/> </a>
                            </div>
                        </div>
                    )
                })}
            </div>
        </Container>
    );
};
