import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";

export default function ContactForm ({ display = true }) {
    const [sended, setSended] = useState(2);
    const { register, formState: { errors }, handleSubmit, reset } = useForm("");

    const onSubmit = async (data) => {
        try {
            const response = await fetch('https://formspree.io/f/moqbjpjj', {
                method: 'post',
                body: JSON.stringify(data),
                headers: {
                    'Accept': 'application/json'
                }
            });
            const jsonData = await response.json();
            setSended( jsonData.ok == true ? true : false );
            reset();
        } catch {
            setSended(false);
            reset();
        }
    }

    let showStatus = false;
    const showError = () => {
        if (showStatus) {
            return false;
        } else {
            showStatus = true;
            return true;
        }
    }

    useEffect(() => {
        showStatus = false;
    });

    const errorMessage = (error, type, message) => error?.type === type && showError() && <p className="errorText">{message}</p>;

    return (
        <div className={`formContainer  ${display ? 'contactFormActive' : 'contactFormTransition'}`}>
            <h3 className='text-secondary formTitle' >Contact Us</h3>
            <form onSubmit={handleSubmit(onSubmit)} method="post" className='formRegister'>
                <input type="text" placeholder="Name" maxLength="50" id="name" {...register('name', { required: true, maxLength: 50 })} className="input" />
                {errorMessage(errors.name, 'required', 'Name field is required')}
                <input type="text" placeholder="Last Name" maxLength="50" id="lastname" {...register('lastname', { required: true, maxLength: 50 })} className="input" />
                {errorMessage(errors.lastname, 'required', 'Last Name field is required')}
                <input type="text" placeholder="Email" maxLength="50" id="email" {...register('email', { required: true, pattern: /^\w+([.-_+]?\w+)*@\w+([.-]?\w+)*(\.\w{2,10})+$/, maxLength: 50 })} className="input" />
                {errorMessage(errors.email, 'required', 'Email field is required')}
                {errorMessage(errors.email, 'pattern', 'Format is wrong')}
                <textarea placeholder="Message" maxLength="300" minLength="10" cols="20" rows="5" id="message" {...register('message', { required: true, minLength: 2, minLength: 10, maxLength: 300 })} className="input textarea"></textarea>
                {errorMessage(errors.message, 'required', 'Message field is required')}
                <input type="submit" value="Submit" className="submit" />
                {(sended == true) ? <p className='sendedOk'>Message sent successfully</p> : (sended == false) ? <p className='sendedFail'>There was a problem</p> : ''}
            </form>
        </div>
    );
};
