import React from 'react';
import './NavHeader.css';
import { Navbar, Container } from 'react-bootstrap';
import Contact from '../Contact/Contact';
import ContactForm from '../ContactForm/ContactForm';

class NavHeader2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = { borderNavbar: false, formCollapse: false };
    }

    borderNavbar = () => {
        if (window.scrollY > 0) {
            this.setState({ borderNavbar: true });
        } else {
            this.setState({ borderNavbar: false });
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.borderNavbar, true);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.borderNavbar);
    }

    formCollapse = () => {
        this.setState({ formCollapse: !this.state.formCollapse });
    }

    render() {
        return (
            <Navbar collapseOnSelect expand='lg' sticky='top' className={`blur border-bottom-transition flex-column ${this.state.borderNavbar ? 'border-bottom-active' : ''} ${this.state.formCollapse ? 'fullHeight' : ''}`}>
                <Container fluid>
                    <a className="navbar-brand text-secondary text-hover-color" href="#"><h1>R-IMAGE</h1></a>
                    <Navbar.Toggle aria-controls='responsive-navbar-nav' />
                    {this.props.children}
                    <Contact active={this.state.formCollapse} callback={this.formCollapse} />
                </Container>
                <ContactForm display={this.state.formCollapse} />
            </Navbar>
        );
    }
}

class NavHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = { borderNavbar: false, formCollapse: false, mainToggle: false, state: '' };
    }
    // this.state.state : '' | 'contact' | 'active'

    borderNavbar = () => {
        if (window.scrollY > 0) {
            this.setState({ borderNavbar: true });
        } else {
            this.setState({ borderNavbar: false });
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.borderNavbar, true);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.borderNavbar);
    }

    mainToggle = () => {
        if (this.state.state === '') {
            this.setState({ state: 'active' });
        } else {
            this.setState({ state: '' });
        }
    }

    formCollapse = () => {
        if (this.state.state === 'contact') {
            this.setState({ state: '' });
        } else {
            this.setState({ state: 'contact' });
        }
    }

    render() {
        let estado = this.state.state;
        let formCollapse = false;
        let navEstado = '';
        if (estado === 'contact') {
            navEstado = 'fullHeight';
            formCollapse = false;
        }

        return (
            <nav className={`sticky-top blur border-bottom-transition flex-column ${this.state.borderNavbar ? 'border-bottom-active' : ''} ${navEstado}`}>
                <Container fluid>
                    <div className='flexDirection'>
                        <div className='d-flex justify-content-between'>
                            <a className="navbar-brand text-secondary text-hover-color" href="#"><h1>R-IMAGE</h1></a>
                            <button type="button" className={`nav-link active text-center text-hover-color customButton d-lg-none ${estado === '' ? 'text-secondary' : 'button-active'}`} onClick={this.mainToggle} >TOGGLE</button>
                        </div>
                        <div className={`flexDirection `}>
                            {this.props.children}
                            <Contact active={formCollapse} callback={this.formCollapse} />
                        </div>
                    </div>
                    <ContactForm display={formCollapse} />
                </Container>
            </nav>
        );
    }
}

/**

                        <div className={`flexDirection ${(estado === '') ? 'collapse2' : 'show2'}`}>


 */


export default NavHeader;
